//src/App.js
//Import libraries and components
import { useState } from 'react';
import { ethers, utils } from "ethers";


function App() {
  //State variables
  const [tokens, setTokens] = useState([])
  const [address, setAddress] = useState('')

  //Function to handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setAddress(address);
    fetchTokens() 
    .then(data => {
      setTokens(data.assets)
      console.log(tokens)
      console.log('success')
      {tokens.map((token, index) => (
        <div key={index}>
          <h2>Name: {token.name}</h2>
          <h2>Name: {utils.formatUnits(token.amount, token.decimals)}</h2>
          <h2>Name: {token.symbol}</h2>
          <hr />
        </div>
      ))}
    })
    .catch(err => setTokens([]))
  }

  //Function to fetch tokens
  const fetchTokens = async () => {
    if (!utils.isAddress(address)){
      alert('Please enter a valid Ethereum wallet address')
      return;
    }
    const provider = new ethers.providers.JsonRpcProvider("https://still-damp-hill.quiknode.pro/f8a47a05afaaa075ee473ab78741f3b48892c278/");
    const tokens = await provider.send("qn_getWalletTokenBalance", {
      wallet: address,
      contracts: [
        '0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2', //WETH
        '0xdAC17F958D2ee523a2206206994597C13D831ec7', //USDT
        '0x7D1AfA7B718fb893dB30A3aBc0Cfc608AaCfeBB0', //MATIC
        '0xC18360217D8F7Ab5e7c516566761Ea12Ce7F9D72', //ENS
      ]
    });
    return tokens
  }

  return(
    <div className="flex h-screen w-screen justify-center space-x-3">  
      <div className="flex justify-center space-x-3 w-screen h-14 mt-10">
        <form 
            onSubmit={handleSubmit}
            className="w-4/12 h-15 relative block overflow-hidden rounded-md border border-gray-200 px-3 pt-3 shadow-sm focus-within:border-blue-600 focus-within:ring-1 focus-within:ring-blue-600 dark:border-gray-700 dark:bg-gray-800"> 
            <input
                onChange={e => setAddress(e.target.value)}
                type="text"
                placeholder="Enter your Address here ðŸŽ¯"
                className="mt-1 w-full border-none p-0 focus:border-transparent focus:outline-none focus:ring-0 sm:text-sm"
            />
            <button
            type='submit'
            className="rounded-lg top-1 right-1 bottom-1 border absolute w-12 justify-center bg-blue-400 text-white p-3 font-bold uppercase"
            >
            GO
            </button>
        </form>
      </div>

    </div>


  );
}

export default App;
